#define QUALCOMM_VENDOR_ID			0x05C6

#define ZTE_USB_DEVICE_ID_MATCH_INT_CLASS_AND_SUBCLASS_INFO(vend,cl, sc) \
         .match_flags = (USB_DEVICE_ID_MATCH_VENDOR)  \
