/* some devices interfaces need special handling due to a number of reasons */
enum zte_blacklist_reason {
		zte_BLACKLIST_NONE = 0,
		zte_BLACKLIST_SENDSETUP = 1,
		zte_BLACKLIST_RESERVED_IF = 2
};

#define MAX_BL_NUM  8
